import java.util.Scanner;
import java.util.ArrayList;

class HangHoa{
    private String maHang , tenHang;
    private double donGia, soLuong;
    
    public HangHoa(){
        this.maHang="";
        this.tenHang="";
        this.donGia=0;
        this.soLuong=0;
    }

    public HangHoa(String maHang, String tenHang, double donGia, double soLuong) {
        this.maHang = maHang;
        this.tenHang = tenHang;
        this.donGia = donGia;
        this.soLuong = soLuong;
    }

    public String getMaHang() {
        return maHang;
    }

    public String getTenHang() {
        return tenHang;
    }

    public double getDonGia() {
        return donGia;
    }

    public double getSoLuong() {
        return soLuong;
    }

    public void setMaHang(String maHang) {
        this.maHang = maHang;
    }

    public void setTenHang(String tenHang) {
        this.tenHang = tenHang;
    }

    public void setDonGia(double donGia) {
        this.donGia = donGia;
    }

    public void setSoLuong(double soLuong) {
        this.soLuong = soLuong;
    }

    public double tongTien(){
        return donGia*soLuong;
    }
    @Override
    public String toString() {
        return "maHang=" + maHang + ", tenHang=" + tenHang + ", donGia=" + donGia + ", soLuong=" + soLuong + ", Tong tien"+tongTien();
    }
    
    
}

class ChucNang{
    private final ArrayList<HangHoa> dshh;
    
    public ChucNang(){
        dshh = new ArrayList<>();
    }
    public void nhapNV(){
        Scanner sc = new Scanner(System.in);
        System.out.print("Nhap ma hang: ");
        String ma = sc.nextLine();
        System.out.print("Nhap ho ten hang: ");
        String ten = sc.nextLine();
        System.out.print("Nhap don gia: ");
        double dg= sc.nextDouble();
        System.out.print("Nhap so luong: ");
        double luong= sc.nextDouble();
        
        HangHoa hh= new HangHoa(ma, ten, dg, luong);
        dshh.add(hh);
    }
    
    public void hienThi(){
        for(HangHoa hh: dshh){
            System.out.print("Thong tin nhan vien: ");
            System.out.println(hh.toString());
        }
    }
    
    public void timKiem(String maHang){
        boolean timThay=false;
        for(HangHoa hh: dshh){
            if(hh.getMaHang().equals(maHang)){
                System.out.print("Thong tin nhan vien tim kiem: ");
                System.out.println(hh.toString());
                timThay=true;
            }
        }
        if(!timThay){
            System.out.println("Khong tim thay nhan vien co ho ten: "+maHang);
        }
    }
    
    
    public void sapXep(){
        dshh.sort((mh1,mh2)->mh1.getTenHang().compareToIgnoreCase(mh2.getTenHang()));
    }
}
public class ontap4 {
    public static void main(String[] args) {
        Scanner sc =new Scanner(System.in);
        ChucNang ct = new ChucNang();
        
        while (true) {            
            System.out.println("----------------MENU------------------");
            System.out.println("1.Nhap hang hoa");
            System.out.println("2.Hien thi hang hoa");
            System.out.println("3.Tim kiem hang hoa theo ma hang");
            System.out.println("6.Sap xep hang hoa");
            System.out.println("0.Thoat chuong trinh");
            System.out.print("Hay chon chuc nang: ");
            
            int luachon=Integer.parseInt(sc.nextLine());
            switch (luachon) {
                case 1:
                    ct.nhapNV();
                    break;
                case 2:
                    ct.hienThi();
                    break;
                case 3:
                    System.out.print("Nhap ma hang hoa can tim: ");
                    String ma = sc. nextLine();
                    ct.timKiem(ma);
                    break;
                case 4:
                    ct.sapXep();
                    System.out.println("Danh sach hang hoa sau khi sap xep la:");
                    ct.hienThi();
                    break;
                case 0:
                    System.out.println("Tam biet! Hen gap lai");
                    System.exit(0);
                default:
                    System.out.println("Lua chon khong dung! Hay chon lai");
            }
        }
    }
}
